import React from 'react'
import App from 'next/app';
import Link from 'next/link'
import Footer from '../components/Footer';
import Head from 'next/head'

import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

import '../../assets/css/style.main.css';

export default class MKApp extends App {
    render() {
        const { Component } = this.props;
        console.log(this.props.stars);
        return (
            <>

                <Head>
                    <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no" />

                    <title>We Are Make !</title>

                    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />

                    <link href="https://fonts.googleapis.com/css?family=Roboto" rel="stylesheet" />


                </Head>

                <Component />

                <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js" ></script>
                <script src="https://cdn.jsdelivr.net/npm/popper.js@1.16.0/dist/umd/popper.min.js" ></script>
                <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.0/js/bootstrap.min.js" ></script>


            </>
        )

    }


    componentDidMount() {
      

        $('[data-tools="true"]').each(function (e) { 
            $('.mk-control_tools').toggleClass('active')
        }).click(function(e){
            e.preventDefault()
            $('.mk-control_tools').toggleClass('active')
        });
    }
   
}

MKApp.getInitialProps = async (ctx) => {
    const res = await fetch('https://api.github.com/repos/vercel/next.js')
    const json = await res.json()
    return { stars: json.stargazers_count }
  }
