import React, { useState } from 'react';
import Nav from '../components/Nav';
import LayoutTools from '../components/layout/tool';
import LayoutMain from '../components/layout/main';


export default function Indexs(props) {

  const [contact, setContact] = useState({
    name: '',
  });

  const handleChange = e => {
    console.log( e.target.value);
  }
    {/* setContact({ ...contact, [e.target.name]: e.target.value }); */}

  const handleSubmit = async e => {
    e.preventDefault();
    console.log(contact)
  };

  return (

    <>
      <Nav />

      <nav className=" d-none d-md-block bg-light mk-sidebar" >
        <div className="mk-sidebar_sticky">


        </div>
      </nav>
      <main role="main" className=" col-md-9 ml-md-auto col-lg-11 mk-control_main mr-3">
        <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
          <h1 className="h2">Dashboard</h1>
          <div className="btn-toolbar mb-2 mb-md-0">

            <form onSubmit={handleSubmit}>
              <div className="input-group mb-2 mr-sm-2">
              <input
                    className='form-control form-control-sm'
                    type='text'
                    placeholder='Name'
                    name='name'
                    onChange={handleChange}
                    required
                />
                <div className="input-group-prepend">
                <input type="submit" value="Submit" className="btn btn-sm btn-outline-secondary " />
                </div>

              </div>



            </form>

          </div>
        </div>
        <div className="row">
          {

            props.data.map(({ show }) => (

              <div className="col-3">
                <div className="card mb-3" style={{ maxWidth: 540 }}>
                  <div className="row no-gutters">
                    <div className="col-md-4 p-0">
                      <img src={show.image.medium} className="card-img-top" alt="..." />
                    </div>
                    <div className="col-md-8">
                      <div className="card-body">
                        <h6 className="card-title">{show._id} {show.name}</h6>
                        <p className="card-text">This is a wider card</p>
                        <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

            ))

          }
        </div>

      </main>


    </>
  )

}

export async function getStaticProps() {

  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman')
  const data = await res.json()

  return {
    props: {
      data,
    },
  }
}
